function addItem(){$("#add-item-error").hide();var n=$("#add-item-title").val(),t=$("#add-item-dueAt").val();$.post("/Todo/AddItem",{title:n,dueAt:t},function(){window.location="/Todo"}).fail(function(n){if(n&&n.responseJSON){var t=n.responseJSON[Object.keys(n.responseJSON)[0]];$("#add-item-error").text(t);$("#add-item-error").show()}})}$(document).ready(function(){$(".datepicker").datepicker();$("#add-item-button").on("click",addItem)});